import React, { Component } from 'react'
import axios from 'axios' 
import Main from '../template/Main'

const headerProps = {
    icon: 'search',
    title: 'Movies',
    subtitle: 'Search Movies by Title'
}

const baseUrl = '//https://localhost:44313/api/movies/search/?title=';
const initialState = {
    respMovie: { quantity: 0, year: 0, total: 0 },
    list:[]
}

export default class Movie extends Component {

    state = { ...initialState }

    clear(){
        this.setState({movie: initialState.movie})
    }

    get(title = '') {
        const search = title ? `&title__regex=/${title}/` : ''
        axios.get(`${baseUrl}?sort=-createdAt${search}`)
        .then(response => { this.setState({ listaItens: response.data}); })
        .catch(() => { console.log('Erro ao recuperar os dados'); });    
    }

    renderForm(){
        return (
            <div className="form">
                <div className="row">
                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label>Title</label>
                            <input type="text" className="form-control" name="title" value={this.state.movie.title}
                            placeholder="Enter the title..."/> 
                        </div>
                    </div>
                </div>

                <hr />
                <div className="row">
                    <div className="col-12 d-flex justify-content-end">
                        <button className="btn btn-primary"/*onClick={e => this.save(e)}>*/>                            
                            Search
                        </button>  

                        <button className="btn btn-secondary ml-2" onClick={e => this.clear(e)}>
                            Cancel
                        </button>
                    </div>
                </div>
            </div>
        )
    }

    render(){

        return(
            <Main {...headerProps}>
                {this.renderForm()}
            </Main>
        )
    }
}
