import React, { Component } from 'react'
import axios from 'axios' 
import Main from '../template/Main'

const headerProps = {
    icon: 'search',
    title: 'Movies',
    subtitle: 'Search Movies by Title'
}

const baseUrl = 'http://localhost:3002/movie';
const initialState = {
    movie: { title: '' },
    list:[]
}

export default class Movie extends Component {

    state = { ...initialState }

    clear(){
        this.setState({movie: initialState.movie})
    }

    renderForm(){
        return (
            <div className="form">
                <div className="row">
                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label>Title</label>
                            <input type="text" className="form-control" name="title" value={this.state.movie.title}
                            placeholder="Enter the title..."/> 
                        </div>
                    </div>
                </div>

                <hr />
                <div className="row">
                    <div className="col-12 d-flex justify-content-end">
                        <button className="btn btn-primary">
                            Search
                        </button>  

                        <button className="btn btn-secondary ml-2">
                            Cancel
                        </button>
                    </div>
                </div>
            </div>
        )
    }

    render(){
        return(
            <Main {...headerProps}>
                Movies
            </Main>
        )
    }
}
